import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { useHistory } from 'react-router-dom'
import { useForm } from "react-hook-form";

const AddProduct = ({ onAdd }) => {

    const { register, handleSubmit, watch, errors } = useForm(); // sử dụng hook fore
    const history = useHistory()

    // const [valueInput, setValueInput] = useState({});

    // const onHandleChange = (e) => {
    //     const {name, value } = e.target;
    //     setValueInput({
    //         ...valueInput,
    //         [name]: value
    //     });
    // }

    const onHandleSubmit = (data) => {
        console.log(data);
        // e.preventDefault(data);
        onAdd(data);
        history.replace('/admin/products')
    }


    return (
        <div className="card shadow my-4 form">
            <div className="card-header py-3">
                <h5 className="m-0 font-weight-bold text-primary"> Thêm sản phẩm</h5>
            </div>
            <div className="card-body">
                <form onSubmit={handleSubmit(onHandleSubmit)}>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Name</label>
                        <input
                            placeholder="Tên sản phẩm"
                            type="text"
                            name="name"
                            className="form-control"
                            // onChange={onHandleChange}  
                            ref={register({ required: true, minLength: 5 })}
                        />
                        {errors.name && errors.name.type === "required" && <span className="text-red">Tên sản phẩm không được để trống</span>}
                        {errors.name && errors.name.type === "minLength" && <span className="text-red">Bạn phải nhập ít nhất 5 ký tự!</span>}
                    </div>
                    <div className="form-group mt-50">
                        <label htmlFor="exampleInputEmail1">Image</label>
                        <input type="file" name="image" className="form-control-file img" ref={register} />
                    </div>
                    <div className="form-group mt-50">
                        <label htmlFor="exampleInputEmail1">Price</label>
                        <input
                            placeholder="Giá tiền"
                            type="text"
                            name="price"
                            className="form-control"
                            // onChange={onHandleChange} 
                            ref={register({ required: true })}
                        />
                        {errors.price && <span className="text-red">Giá sản phẩm không được để trống!</span>}
                    </div>
                    <button
                        type="submit"
                        className="btn btn-primary mt-50"
                    >
                        Thêm sản phẩm
                    </button>
                </form>



               

            </div>



            
        </div>
    )
}

AddProduct.propTypes = {
}

export default AddProduct
